import React, { useEffect, useState } from 'react';
import './App.css';
import axios from 'axios';
import { Link ,Router,Route,BrowserRouter} from 'react-router-dom';
import Edit from './Edit';
import Home from './home';

function Fetchtable() {
    const [rec, setRec] = useState([]);

    const fetch = () => {
        axios.get('http://localhost:5050/api/call/userinfo/')
            .then(res => setRec(res.data))
            .catch(err => console.log(err));
    }
    useEffect(() => {
        fetch();
    }, [])
    function del(email) {
        alert("delete the record");
        const url = 'http://localhost:5050/api/call/userinfo/delete/' + email;
        console.log(url);
        axios.delete(url)
            .then(res => console.log("deleted"))
            .catch(error => console.log(error));
    fetch();
    }


    return (
        <BrowserRouter>
        <Router>

        <div className='tabledesign'>

            <table>
                <thead>
                    <tr>
                        <th>Email</th>
                        <th>FirstName</th>
                        <th>LastName</th>
                        <th>Mobile</th>
                        <th>DOB</th>
                        <th>Address</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        rec.map((user, index) => (
                            <tr key={index}>
                                <td>{user.email}</td>
                                <td>{user.firstname}</td>
                                <td>{user.lastname}</td>
                                <td>{user.mobile}</td>
                                <td>{user.dob}</td>
                                <td>{user.address}</td>
                                <td>
                                    <button onClick={(e) => { del(user.email); }} >Delete</button>
                                    <Link to='/Edit'><button >Edit</button></Link>
                                </td>
                            </tr>
                        ))

                    }
                </tbody>
            </table>
            <Route path="/" element={<Home />} />
            <Route path='/Edit' element={<Edit />}/>
        </div>
        </Router>
        /BrowserRouter></BrowserRouter>
    )
}
export default Fetchtable;
